  color: $color-against-primary

.homeStats__title
  margin-bottom: $pad

.homeStats__subtitle
  opacity: 0.7
  max-width: 1024px
  display: inline-block
  @include from($tablet-landscape)
    font-size: 1.2rem

.homeStats__stats
  display: flex
  justify-content: center
  flex-wrap: wrap
.homeStats__stat
  margin: $pad
  text-align: center
  width: 10rem

.stat__icon
  margin: auto

.stat__number
  font-size: 2rem
  font-weight: bold

.stat__desc
  font-weight: bold
  opacity: 0.7
</style>

